package apjfsa;

public class Swapandadition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int a=10;
		int b=25;
		
		System.out.println("Before Swap");
		System.out.println("a: " + a);
		System.out.println("b: " + b);
		
		int c = a;
		a = b;
		b =c;
		
		System.out.println("After swap");
		System.out.println("a: " + a);
		System.out.println("b: " + b);
            
	}

}
